<template>  <!--  class="chart"-->  <div ref="chart" style="width: 37rem; height: 16rem;"></div></template><script setup>import { ref, onMounted } from 'vue';import * as echarts from 'echarts';import axios from "axios";const chart = ref(null);onMounted(async() => {  const myChart = echarts.init(chart.value, null, {    renderer: 'svg',    devicePixelRatio: window.devicePixelRatio > 1 ? 2 : 1  });  let data;  try {    const response = await axios.post('/api/homeData/getBranchBarData');    if (response.data.code === 200){      data = response.data.data;    }  } catch (error) {    console.error(error);  }  const names = data.map(item => item.name);  const values = data.map(item => item.value);  // ECharts 配置项  const option = {    title: {      text: '总分店/院编码待补充',      left: 'center',      textStyle: {        color: '#333333',        fontSize: 13,        fontWeight: 'bold',        fontFamily: 'Arial, sans-serif',      },    },    tooltip: {      trigger: 'axis',      axisPointer: {        type: 'shadow'      },      textStyle: {        color: '#333333',        fontFamily: 'Arial, sans-serif',        fontSize: 12,      },    },    legend: {      data: ['编码待补充'],      bottom: 10,      textStyle: {        color: '#333333',        fontFamily: 'Arial, sans-serif',        fontSize: 12,      },      itemWidth: 20,      itemHeight: 10,    },    grid: {      left: '3%',      right: '4%',      bottom: '15%',      top: '20%',      containLabel: true,      backgroundColor: '#F5F5F5',      borderColor: '#E0E0E0',    },    xAxis: {      type: 'category',      data: names,      axisLine: {        lineStyle: {          color: '#E0E0E0',        },      },      axisLabel: {        color: '#333333',        fontFamily: 'Arial, sans-serif',        fontSize: 12,      },    },    yAxis: {      type: 'value',      axisLine: {        lineStyle: {          color: '#E0E0E0',        },      },      axisLabel: {        color: '#333333',        fontFamily: 'Arial, sans-serif',        fontSize: 12,      },      splitLine: {        lineStyle: {          color: '#E0E0E0',        },      },    },    series: [      {        name: '编码待补充',        type: 'bar',        data: values.map((value, index) => ({          value: value,          itemStyle: {            color: '#6da1e4'          }        })),        itemStyle: {          borderRadius: [2, 2, 0, 0],          color: '#6da1e4'        },        // 移除了 emphasis 配置项，这样鼠标悬浮时颜色就不会变化        label: {          show: false,          position: 'top',          fontFamily: 'Arial, sans-serif',          fontSize: 12,          fontWeight: 'bold'        },        barWidth: '35%'      }    ]  };  myChart.setOption(option);  window.addEventListener('resize', function () {    myChart.resize();  });});</script><style scoped>/* 可以在这里添加样式 */.chart {  width: 100%;  height: 100%;}</style>